#!/bin/sh
#
# v122_workflowrules.sh
#
# Project: BluebirdCRM
# Author: Brian Shaughnessy
# Organization: New York State Senate
# Date: 2011-07-02
#

prog=`basename $0`
script_dir=`dirname $0`
execSql=$script_dir/execSql.sh
readConfig=$script_dir/readConfig.sh
drush=$script_dir/drush.sh

. $script_dir/defaults.sh

if [ $# -ne 1 ]; then
  echo "Usage: $prog instanceName" >&2
  exit 1
fi

instance="$1"

if ! $readConfig --instance $instance --quiet; then
  echo "$prog: $instance: Instance not found in config file" >&2
  exit 1
fi

###### Begin Update Scripts ######

### Drupal ###

## set workflow rules
rules="TRUNCATE TABLE rules_rules;
INSERT INTO rules_rules VALUES
('rules_notify_creator_of_approval', ),
('rules_notify_creator_of_rejection', );"
$execSql -i $instance -c "$rules" --drupal

### Cleanup ###

$script_dir/fixPermissions.sh
$script_dir/clearCache.sh $instance
